var signedIn = new State(() => browser.HasContent("Signed in as:"));
var signedOut = new State(() => browser.HasContent("Please sign in"));

if (browser.FindState(signedIn,signedOut) == signedIn) 
{
  browser.ClickLink("Sign out");
}

